def create_ctrl(
        name, side, index, size, color, shape, cvs_pos, axis,
        lock_attrs, parent, animation_set, translation=(0, 0, 0), rotation=(0, 0, 0), scale=(1, 1, 1)
):
    """Create controller based on arguments, ctrl_side_name_index

    Args:
        name (str): Controller name.
        side (str/None): Controller side.
        index (int/None): Controller index.
        size (int): Controller size.
        color (int): Controller color.
        shape (str): Controller shape.
        cvs_pos (list/None): Controller shape cvs positions list
        axis (str): Controller orientation. 'X+'/'X-'/'Y+'/'Y-'/'Z+'/'Z-'
        lock_attrs (list): Controller attributes to lock.
        parent (str/None): Controller's upper level parent.
        animation_set (str/None): Animation controllers set
        translation (tuple): Controller's world translation position.
        rotation (tuple): Controller's world rotation position.
        scale (tuple): Controller's scale position.


    Raises:
        ValueError: If controller name already exists.

    Returns:
        str: Controller full name

    """

    # get side
    if side and side.lower() != 'none':
        side = '{}_'.format(side)
    else:
        side = ''

    # get index
    if type(index) == int:
        index = '_{:03d}'.format(index)
    else:
        index = ''

    ctrl_name = 'ctrl_{}{}{}'.format(side, name, index)

    if cmds.objExists(ctrl_name):
        raise ValueError('{} already exists in the scene'.format(ctrl_name))

    else:
        if shape == 'Custom':
            pos_list = cvs_pos
        else:
            pos_list = get_ctrl_shape_pos(shape=shape)

        if pos_list:
            ctrl = cmds.curve(name=ctrl_name, p=pos_list, d=1)
            cmds.rename(cmds.listRelatives(ctrl, shapes=True)[0], '{}Shape'.format(ctrl))
            # set size, color, axis
            cmds.setAttr('{}.s'.format(ctrl), size, size, size)
            if axis == 'X+':
                cmds.setAttr('{}.rx'.format(ctrl), 90)
            elif axis == 'X-':
                cmds.setAttr('{}.rx'.format(ctrl), -90)
            elif axis == 'Y+':
                cmds.setAttr('{}.ry'.format(ctrl), 90)
            elif axis == 'Y-':
                cmds.setAttr('{}.ry'.format(ctrl), -90)
            elif axis == 'Z+':
                cmds.setAttr('{}.rz'.format(ctrl), 90)
            elif axis == 'Z-':
                cmds.setAttr('{}.rz'.format(ctrl), -90)

            cmds.makeIdentity(ctrl_name, apply=True, t=True, r=True, s=True, n=False, pn=True)

            # set color
            cmds.setAttr('{}Shape.overrideEnabled'.format(ctrl), True)
            cmds.setAttr('{}Shape.overrideColor'.format(ctrl), color)

        else:
            ctrl = cmds.group(name=ctrl_name, empty=True)

        # add group above
        offset_grp = add_extra_group(
            obj=ctrl, grp_name='{}_001'.format(ctrl.replace('ctrl', 'offset')), world_orient=False
        )
        connect_grp = add_extra_group(
            obj=offset_grp, grp_name=offset_grp.replace('offset', 'connect'), world_orient=False
        )
        driven_grp = add_extra_group(
            obj=connect_grp, grp_name=connect_grp.replace('connect', 'driven'), world_orient=False
        )
        zero_grp = add_extra_group(
            obj=driven_grp, grp_name=driven_grp.replace('driven', 'zero'), world_orient=False
        )

        # lock attrs
        lock_and_hide_attrs(obj=ctrl, attrs_list=['rotateOrder'], lock=False, hide=False)
        lock_and_hide_attrs(obj=ctrl, attrs_list=lock_attrs, lock=True, hide=True)

        # add subctrl
        sub_ctrl = cmds.duplicate(ctrl, name=ctrl.replace(name, '{}Sub'.format(name)))[0]
        if pos_list:
            cmds.select('{}.cv[*]'.format(sub_ctrl))
            cmds.scale(0.9, 0.9, 0.9, r=True)
            cmds.setAttr('{}Shape.overrideEnabled'.format(sub_ctrl), True)
            cmds.setAttr('{}Shape.overrideColor'.format(sub_ctrl), 3)

        # organize subctrl
        cmds.parent(sub_ctrl, ctrl, a=True)
        cmds.addAttr(ctrl, ln='subCtrl', at='bool', keyable=False, hidden=False)
        cmds.setAttr('{}.subCtrl'.format(ctrl), e=True, channelBox=True)
        cmds.connectAttr('{}.subCtrl'.format(ctrl), '{}.v'.format(sub_ctrl))

        # add output group
        output_grp = cmds.group(name=ctrl.replace('ctrl', 'output'), empty=True)
        cmds.parent(output_grp, ctrl, a=True)
        # connect sub_ctrl to output_grp
        for attr in ['tx', 'ty', 'tz', 'rx', 'ry', 'rz', 'sx', 'sy', 'sz']:
            cmds.connectAttr('{}.{}'.format(sub_ctrl, attr), '{}.{}'.format(output_grp, attr))

        lock_and_hide_attrs(
            obj=output_grp,
            attrs_list=['tx', 'ty', 'tz', 'rx', 'ry', 'rz', 'sx', 'sy', 'sz', 'v'],
            lock=True,
            hide=True
        )

        # clean up
        for obj in [ctrl, sub_ctrl, output_grp, offset_grp, connect_grp, driven_grp, zero_grp]:
            lock_and_hide_attrs(obj=obj, attrs_list=['v'], lock=True, hide=True)

        # set position
        cmds.xform(zero_grp, scale=scale)
        cmds.xform(zero_grp, ws=True, a=True, translation=translation, rotation=rotation)

        # parent to parent
        if parent:
            parent_object(parent_obj=parent, child_obj=zero_grp)
            lock_and_hide_attrs(
                obj=zero_grp,
                attrs_list=['tx', 'ty', 'tz', 'rx', 'ry', 'rz', 'sx', 'sy', 'sz', 'v'],
                lock=True,
                hide=True
            )

        # parent to set
        if animation_set:
            animation_ctrls_set = animation_set
            if not cmds.objExists(animation_set) or cmds.nodeType(animation_set) != 'objectSet':
                animation_ctrls_set = cmds.sets(name=animation_set, empty=True)

            cmds.sets(ctrl, edit=True, forceElement=animation_ctrls_set)
            cmds.sets(offset_grp, edit=True, forceElement=animation_ctrls_set)

        return ctrl


def add_extra_group(obj, grp_name, world_orient=False):
    """Add an extra group above the object.

    Args:
        obj (str): Maya object to add an extra group.
        grp_name (str): The extra group name
        world_orient (bool): Set the new group world oriented or not.

    Returns:
        str: The newly added group.

    """

    obj_grp = cmds.group(name=grp_name, empty=True)
    t_pos = cmds.xform(obj, q=True, ws=True, t=True)
    r_pos = cmds.xform(obj, q=True, ws=True, ro=True)
    if world_orient:
        r_pos = [0, 0, 0]
    s_pos = cmds.xform(obj, q=True, ws=True, s=True)
    cmds.xform(obj_grp, s=s_pos)
    cmds.xform(obj_grp, ws=True, t=t_pos)
    cmds.xform(obj_grp, ws=True, ro=r_pos)

    obj_parent = cmds.listRelatives(obj, parent=True)
    if obj_parent:
        cmds.parent(obj_grp, obj_parent[0], absolute=True)
        cmds.parent(obj, obj_grp, absolute=True)
    else:
        cmds.parent(obj, obj_grp, absolute=True)

    return obj_grp


def lock_and_hide_attrs(obj, attrs_list, lock=True, hide=True):
    """Lock or unlock, hide or show attributes.

    Args:
        obj (str): Object which has a list of attributes need to be lock/hide or unlock/show.
        attrs_list (list): A list of attributes need to be lock/hide or unlock/show.
        lock (bool): Lock or unlock the attributes.
        hide (bool): Hide or show the attributes.

    """

    for attr in attrs_list:
        if cmds.objExists('{}.{}'.format(obj, attr)):
            if lock:
                cmds.setAttr("{}.{}".format(obj, attr), lock=True)
            if hide:
                cmds.setAttr("{}.{}".format(obj, attr), keyable=False, channelBox=False)
            if not lock:
                cmds.setAttr("{}.{}".format(obj, attr), lock=False)
            if not hide:
                cmds.setAttr("{}.{}".format(obj, attr), keyable=True)


def parent_object(parent_obj, child_obj):
    """Parent object.

    Args:
        parent_obj (str): The parent object.
        child_obj (str): The child object.

    """

    parents = cmds.listRelatives(child_obj, parent=True)
    if parents:
        if parents[0] != parent_obj:
            cmds.parent(child_obj, parent_obj)
    else:
        cmds.parent(child_obj, parent_obj)
       